  });

  // AI í‚¤ì›Œë“œì™€ ë³‘í•©
  aiKeywords.forEach((kw) => {
    wordCount[kw.word] = (wordCount[kw.word] || 0) + kw.count;
  });

  // ìƒìœ„ 10ê°œ ì¶”ì¶œ
  return Object.entries(wordCount)
    .sort(([, a], [, b]) => b - a)
    .slice(0, 10)
