export interface Message {
  date: string;
  user: string;
  message: string;
}

export interface Batch {
  batchId: number;
  messages: Message[];
  count: number;
}

/**
 * ë©”ì‹œì§€ ë°°ì—´ì„ ì„¸ì…˜ ê¸°ë°˜ìœ¼ë¡œ ìŠ¤ë§ˆíŠ¸ ë¶„í• 
 * @param messages ë©”ì‹œì§€ ë°°ì—´
 * @param targetSize ëª©í‘œ ë°°ì¹˜ í¬ê¸° (ê¸°ë³¸ê°’: 2000)
 * @returns ë¶„í• ëœ ë°°ì¹˜ ë°°ì—´ (1800~2200ê°œ ìœ ì—°í•œ í¬ê¸°)
 */
export function splitBySession(messages: Message[], targetSize = 2000): Batch[] {
  const batches: Batch[] = [];
  let currentBatch: Message[] = [];
  let batchId = 1;
  const maxSize = 2200; // ìµœëŒ€ ë°°ì¹˜ í¬ê¸°

  for (let i = 0; i < messages.length; i++) {
    const currentMsg = messages[i];
    currentBatch.push(currentMsg);

    // ë‹¤ìŒ ë©”ì‹œì§€ í™•ì¸
    const nextMsg = messages[i + 1];

    // ë°°ì¹˜ ì™„ë£Œ ì¡°ê±´
    let shouldFinalizeBatch = false;

    if (!nextMsg) {
      // ë§ˆì§€ë§‰ ë©”ì‹œì§€ë©´ ë°°ì¹˜ ì™„ë£Œ
      shouldFinalizeBatch = true;
    } else if (currentBatch.length >= maxSize) {
      // 2200ê°œ ë„ë‹¬ ì‹œ ë¬´ì¡°ê±´ ë°°ì¹˜ ì™„ë£Œ (ìƒí•œì„ )
      shouldFinalizeBatch = true;
    } else if (currentBatch.length >= targetSize) {
      // 2000ê°œ ë„ë‹¬ ì‹œ ë‹¤ìŒ ë©”ì‹œì§€ì™€ ì„¸ì…˜ ì¢…ë£Œ ì—¬ë¶€ ì²´í¬
      const gapToNext = calculateGapMinutes(currentMsg, nextMsg);
      const isNextSessionEnd = isSessionEnd(currentMsg, nextMsg, gapToNext);

      if (isNextSessionEnd) {
        // ì„¸ì…˜ ëì´ë©´ ë°°ì¹˜ ì™„ë£Œ
        shouldFinalizeBatch = true;
      } else {
        // ì„¸ì…˜ ì¤‘ê°„ì´ë©´ ê³„ì† ì¶”ê°€ (2200ê¹Œì§€)
        shouldFinalizeBatch = false;
      }
    }

    if (shouldFinalizeBatch) {
      batches.push({
        batchId: batchId++,
        messages: [...currentBatch],
        count: currentBatch.length,
      });
      currentBatch = [];
    }
  }

  // ë‚¨ì€ ë©”ì‹œì§€ ì²˜ë¦¬
  if (currentBatch.length > 0) {
    batches.push({
      batchId: batchId,
      messages: currentBatch,
      count: currentBatch.length,
    });
  }

  return batches;
}

/**
 * ì„¸ì…˜ ì¢…ë£Œ ì—¬ë¶€ íŒë‹¨
 * @param prevMsg ì´ì „ ë©”ì‹œì§€
 * @param currentMsg í˜„ì¬ ë©”ì‹œì§€
 * @param gapMinutes ì‹œê°„ ê°„ê²© (ë¶„)
 * @returns ì„¸ì…˜ ì¢…ë£Œ ì—¬ë¶€
 */
export function isSessionEnd(
  prevMsg: Message,
  currentMsg: Message,
  gapMinutes: number
): boolean {
  // 30ë¶„ ì´í•˜: ê°™ì€ ì„¸ì…˜
  if (gapMinutes <= 30) {
    return false;
  }

  // 6ì‹œê°„ ì´ìƒ: ë‹¤ë¥¸ ì„¸ì…˜
  if (gapMinutes >= 360) {
    return true;
  }

  // ì¢…ê²° ë©”ì‹œì§€ + 30ë¶„ ì´ìƒ: ë‹¤ë¥¸ ì„¸ì…˜
  if (hasClosingMessage(prevMsg.message) && gapMinutes >= 30) {
    return true;
  }

  // 30ë¶„~6ì‹œê°„ ì‚¬ì´: ì‹œê°„ëŒ€ì™€ ê°„ê²©ìœ¼ë¡œ íŒë‹¨
  const prevDate = new Date(prevMsg.date);
  const currentDate = new Date(currentMsg.date);
  const prevPeriod = getTimePeriod(prevDate.getHours());
  const currentPeriod = getTimePeriod(currentDate.getHours());

  // ê°™ì€ ì‹œê°„ëŒ€ + 2ì‹œê°„ ì´ë‚´: ê°™ì€ ì„¸ì…˜
  if (prevPeriod === currentPeriod && gapMinutes <= 120) {
    return false;
  }

  // ê·¸ ì™¸: ë‹¤ë¥¸ ì„¸ì…˜
  return true;
}

/**
 * ì‹œê°„ëŒ€ êµ¬ë¶„
 * @param hour ì‹œê°„ (0-23)
 * @returns ì‹œê°„ëŒ€ ë¬¸ìì—´
 */
export function getTimePeriod(hour: number): string {
  if (hour >= 6 && hour < 11) return 'ì•„ì¹¨';
  if (hour >= 11 && hour < 14) return 'ì ì‹¬';
  if (hour >= 14 && hour < 18) return 'ì˜¤í›„';
  if (hour >= 18 && hour < 23) return 'ì €ë…';
  return 'ë°¤'; // 23-6
}

/**
 * ì¢…ê²° ë©”ì‹œì§€ íŒ¨í„´ í™•ì¸
 * @param msg ë©”ì‹œì§€ ë‚´ìš©
 * @returns ì¢…ê²° ë©”ì‹œì§€ ì—¬ë¶€
 */
export function hasClosingMessage(msg: string): boolean {
  const patterns = [
    /ì˜\s*ì/,
    /ê³ ë§ˆì›Œ?/,
    /ã…‡ã…‹/,
    /ë‚˜ì¤‘ì—/,
    /ë°”ì´/,
    /ã„±ã……/,
    /ì•Œì•˜ì–´/,
    /ë°”ë¹ /,
    /ê°€ì•¼?ë¼?/,
  ];

  return patterns.some(pattern => pattern.test(msg));
}

/**
 * ë‘ ë©”ì‹œì§€ ê°„ ì‹œê°„ ì°¨ì´ ê³„ì‚° (ë¶„ ë‹¨ìœ„)
 * @param prevMsg ì´ì „ ë©”ì‹œì§€
 * @param currentMsg í˜„ì¬ ë©”ì‹œì§€
 * @returns ì‹œê°„ ì°¨ì´ (ë¶„)
 */
export function calculateGapMinutes(prevMsg: Message, currentMsg: Message): number {
  const prevDate = new Date(prevMsg.date);
  const currentDate = new Date(currentMsg.date);

  const diffMs = currentDate.getTime() - prevDate.getTime();
  const diffMinutes = Math.abs(diffMs / (1000 * 60));

  return diffMinutes;
}

